  verlag(id: $id) {
      name
      gruendungsdatum
      hauptsitz {
          plz
          ort
          land
      }
  buecher(first: $first) {
      id
      isbn13
      haupttitel  
      nebentitel
      erscheinungsdatum
      auflage
      preis {
          bruttobetrag
          waehrung
      }
      kategorie
      seitenzahl
